// Úvodní přivítání.
$('.messages-list').html('<li class="messages-you clearfix"><span class="float-left message-img"><img src="data:image/png;base64," class="avatar-sm rounded-circle"></span><div class="message-body clearfix"><div class="message-header"><strong class="float-left messages-title">Bot Bob</strong> <small class="time-messages text-muted"><span class="fas fa-time"></span> <span class="minutes">' + getCurrentTime() + '</span></small> </div><p class="float-left rounded">Ahoj, já jsem Bob a těším se na naši konverzaci.</p></div></li>');
        
// Kontrola zmáčknuté klávesy Enter.
document.getElementById('userMsg').addEventListener("keyup", function (event) {
    event.keyCode === 13 ? send_msg() : '';
});

// Získání aktuálního času.
function getCurrentTime() {
    var now = new Date();
            var hh = now.getHours();
            var min = now.getMinutes();
            if (min < 10) {
    min = '0' + min;
    }
    return 'Odesláno: ' + hh + ':' + min;
}

// Odeslání otázky na Boba.
function send_msg() {
    
    // Kontrola prázdné zprávy od uživatele.
    if ($('#userMsg').val().replace(/\s/g, "") === "") return;
    
    // Pomocná proměnná s uživatelskou zprávou.
    var userMsg = $('#userMsg').val();

    // Vložení zprávy do výpisu chatu.
    $('.messages-list').append('<li class="messages-me clearfix"><span class="float-right message-img"><img src="data:image/png;base64," class="avatar-sm rounded-circle"></span><div class="message-body clearfix"><div class="message-header text-right"><strong class="float-right messages-title">Já</strong> <small class="time-messages text-muted"><span class="fas fa-time"></span> <span class="minutes">' + getCurrentTime() + '</span></small> </div><p class="float-right rounded">' + userMsg + '</p></div></li>');
    $('#userMsg').val('');

    // Vzdálený dotaz na server s výpisem odpovědi.
    $.ajax({
        url: 'bot/',
        type: 'POST',
        data: 'userMsg=' + userMsg,
        success: function (result) {
            $('.messages-list').append('<li class="messages-you clearfix"><span class="float-left message-img"><img src="data:image/png;base64," class="avatar-sm rounded-circle"></span><div class="message-body clearfix"><div class="message-header"><strong class="float-left messages-title">Bot Bob</strong> <small class="time-messages text-muted"><span class="fas fa-time"></span> <span class="minutes">' + getCurrentTime() + '</span></small> </div><p class="float-left rounded">' + result["botMsg"] + '</p></div></li>');
            $('.messages-box').scrollTop($('.messages-box')[0].scrollHeight);
        }
    });
}